from flask import Flask, render_template, flash, redirect
from forms import SearchForm, LoginForm
from config import Config
from flask_sqlalchemy import SQLAlchemy

app = Flask(__name__)
app.config.from_object(Config)
app.config["DEBUG"] = True

db = SQLAlchemy(app)

""" @app.route("/index")
def index():
    return render_template("index.html") 
"""
user = {'firstname': 'Jessica'}

@app.route('/login')
def login():
    form = LoginForm()
    if form.validate_on_submit():
        flash('Login requested for user {}, remember_me={}'.format(
            form.username.data, form.remember_me.data))
        return redirect('/dashboard')
    return render_template('login.html', title='Sign In', user=user,form=form)

@app.route('/index')
@app.route("/", methods=['GET', 'POST'])
def index():
    form = SearchForm()
    if form.validate_on_submit():
        flash('Please enter search term'.format(
            form.search_term.data))
        return redirect('/dashboard')
    return render_template("home.html", title="Home", user=user, form=form)

@app.route("/dashboard")
def dashboard():
    return render_template("dashboard.html")

if __name__ == "__main__":
    app.run(debug=True)
